import { Table, DropdownItem, DropdownMenu, DropdownToggle, Dropdown } from "reactstrap";
import React, { ChangeEvent, useState, useEffect, dropDownOpen } from "react";
import "../App.css";

import {gWays} from "../Gateway/GatewaysForm";


export default function LocalView(){

        const [dropdownOpen, setDropdownOpen] = useState(false);
        const [options, setOptions] = useState([]);

        const toggle = () => { setDropdownOpen(!dropdownOpen) }

        return <div>

            <Dropdown isOpen={dropdownOpen} toggle={toggle}>
                <DropdownToggle caret>
                    Dropdown
                </DropdownToggle>
                <DropdownMenu>
                    {gWays?.map(gWay=> {
                    return <DropdownItem>{gWay}</DropdownItem>
                    })}
                </DropdownMenu>
            </Dropdown>

         <Table className="align-items-center" responsive hover striped>

             <thead className="thead-light">
                     <tr>
                         <th scope="col">Gateway</th>
                         <th scope="col">Mac Address</th>
                         <th scope="col">IP Address</th>

                     </tr>
                 </thead>
                 <tbody>
         {gWays?.map((gWay) => {
                       return (
                           <tr key={gWay.ipAdd}>
                               <th scope="row">
                                   {gWay.nameOf}
                               </th>
                              <th scope="row">
                                   {gWay.macAdd}
                              </th>
                              <th scope="row">
                                   {gWay.ipAdd}
                              </th>
                              <th scope="row">
                             </th>
                           </tr>
                       );
                   })}
                 </tbody>
             </Table>
    </div>
}
